Literal.Value:
'{
  "$schema": "https://vega.github.io/schema/vega-lite/v5.json",
  "description": "Centered Vertical Half-Gauge Chart showing Growth vs Target in Millions",
  "width": 280,
  "height": 60,
  "data": {
    "name": "dataset"
  },
  "transform": [
    {
      "calculate": "min(datum.Growth / datum[''Target Yearly''], 1)",
      "as": "ratio"
    },
    {
      "calculate": "datum.ratio * 180",
      "as": "angle"
    },
    {
      "calculate": "round(datum.Growth / 100000) / 10",
      "as": "growth_million"
    },
    {
      "calculate": "datum.growth_million + '' M+''",
      "as": "growth_label"
    }
  ],
  "layer": [
    {
      "mark": {
        "type": "arc",
        "innerRadius": 90,
        "outerRadius": 70,
        "startAngle": -1.5708,
        "endAngle": 1.5708,
        "cornerRadius": 20
      },
      "encoding": {
        "color": {
          "value": "#e6e6e6"
        }
      }
    },
    {
      "mark": {
        "type": "arc",
        "innerRadius": 90,
        "outerRadius": 70,
        "startAngle": -1.5708,
        "endAngle": {
          "expr": "(datum.angle - 90) * (3.14159 / 180)"
        },
        "cornerRadius": 20
      },
      "encoding": {
        "color": {
          "value": "#00528D"
        }
      }
    },
    {
      "mark": {
        "type": "text",
        "align": "center",
        "baseline": "middle",
        "dx": 0,
        "dy": 0,
        "fontSize": 20,
        "fontWeight": "bold"
      },
      "encoding": {
        "text": {
          "field": "growth_label",
          "type": "nominal"
        },
        "color": {
          "value": "#F47524"
        }
      }
    },
    {
      "mark": {
        "type": "text",
        "align": "center",
        "baseline": "top",
        "dy": -40,
        "dx": 0,
        "fontSize": 20
      },
      "encoding": {
        "text": {
          "value": "YTD"
        }
      }
    },
    {
      "mark": {
        "type": "rect",
        "width": 60,
        "height": 24,
        "cornerRadius": 6
      },
      "encoding": {
        "x": {
          "value": 60
        },
        "y": {
          "value": 44
        },
        "fill": {
          "value": "#e6e6e6"
        }
      }
    },
    {
      "mark": {
        "type": "rect",
        "width": 60,
        "height": 24,
        "cornerRadius": 6
      },
      "encoding": {
        "x": {
          "value": 220
        },
        "y": {
          "value": 44
        },
        "fill": {
          "value": "#e6e6e6"
        }
      }
    },
    {
      "mark": {
        "type": "text",
        "align": "center",
        "baseline": "bottom",
        "dx": -80,
        "dy": 23,
        "fontSize": 18,
        "fontWeight": "bold"
      },
      "encoding": {
        "text": {
          "value": "0.0 M"
        },
        "color": {
          "value": "#00528D"
        }
      }
    },
    {
      "mark": {
        "type": "text",
        "align": "center",
        "baseline": "top",
        "dx": 80,
        "dy": 7,
        "fontSize": 18,
        "fontWeight": "bold"
      },
      "encoding": {
        "text": {
          "value": "1 M"
        },
        "color": {
          "value": "#00528D"
        }
      }
    }
  ],
  "view": {
    "stroke": "transparent"
  }
}'
